/*slick-slider  */

.slick-slider {
    position: relative;
    display: block;
    box-sizing: border-box;
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
    -webkit-touch-callout: none;
    -khtml-user-select: none;
    -ms-touch-action: pan-y;
    touch-action: pan-y;
    -webkit-tap-highlight-color: transparent;
}
.slick-prev:before {
    content: "\f053";
    display: inline-block;
    font-size: 20px;
    line-height: 1;
transition: 0.3s;}
.slick-slider .slick-track, .slick-slider .slick-list {
    -webkit-transform: translate3d(0, 0, 0);
    -moz-transform: translate3d(0, 0, 0);
    -ms-transform: translate3d(0, 0, 0);
    -o-transform: translate3d(0, 0, 0);
    transform: translate3d(0, 0, 0);
}
.slick-list {
    position: relative;
    display: block;
    overflow: hidden;
    margin: 0;
    padding: 0;
}
.slick-track {
    position: relative;
    top: 0;
    left: 0;
    display: block;
}
.slick-initialized .slick-slide {
    display: block;
}
.slick-slide {
    display: none;
    float: left;
    height: 100%;
    min-height: 1px;
}

.slick-slide img {
    display: block;
}
.slick-prev {
    left: 25px;
}
.slick-arrow {
    position: absolute;
    top: 50%;
    -webkit-transform: translateY(-50%);
    transform: translateY(-50%);
    z-index: 2;
    display: inline-block;
    width: 30px;
    height: 30px;
    font-size: 0;
    font-family: "FontAwesome";
    line-height: 0;
    border: none;
    color: #fff;
    background: none;
    transition: 0.3s;
}
.slick-prev:before {
    content: "\f053";
    display: inline-block;
    font-size: 20px;
    line-height: 1;
    transition: 0.3s;
}
.slick-track:before, .slick-track:after {
    display: table;
    content: '';
}
/*end-of-slick-slider  */


.bg-default  a {
    color: #000;
    transition: .3s;
}

.bg-default  img {
    width: 100%;
    height: auto;
}

.carousel-thumbnail .slick-prev {
    left: 0;
    color: #b7b7b7;
    transition: 0.3s;
}
.carousel-thumbnail {
    margin-top: 30px;
    padding: 0 35px;
}
.carousel-thumbnail .slick-slide.slick-current, .carousel-thumbnail .slick-slide:hover {
    opacity: 1;
}
.carousel-thumbnail .slick-slide {
    position: relative;
    padding: 0 10px;
    transition: .3s;
    opacity: .5;
    cursor: pointer;
}
.slick-next:before {
    content: "\f054";
    display: inline-block;
    font-size: 20px;
    line-height: 1;
    transition: 0.3s;
}


